package org.functions.API;import java.util.List;import java.util.Random;public class Pay {    public static void main(String[] args) throws InterruptedException {        Random r = new Random();        int minloc = 5;        int maxloc = 500;        String ran = "";        int max = 0;        int min = 0;        for (int i = 0;i < 2;i++) {            max = r.nextInt(maxloc+1);            min = r.nextInt(minloc+1);        }        for (int i = 0;i <2;i++) {            if (r.nextInt(2) == 0) {                if (r.nextInt(2) == 1) {                    min = Integer.parseInt("-"+min);                } else {                    max = Integer.parseInt("-"+max);                }            }        }        int i = 0;        if (min <0 || max < 0) {            i = max + min;        }        if (max > 0 || min > 0) {            i = max - min;        }        System.out.println(max);        System.out.println(min);        System.out.println(i);    }}