package org.functions.API;import org.bukkit.World;public final class location {    private World World;    private double x;    private double y;    private double z;    private float yaw;    private float pitch;    public location(World World, double x, double y, double z, float yaw, float pitch) {        this.World = World;        this.x = x;        this.y = y;        this.z = z;        this.yaw = yaw;        this.pitch = pitch;    }}