package org.functions.Commands.Permissions;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;import org.functions.Main.Functions;public class CommandFly implements CommandExecutor {    private Functions p = Functions.getMain();    public CommandFly() {    }    public boolean onCommand(CommandSender sender, Command cmd, String s, String[] args) {        if (cmd.getName().equalsIgnoreCase("fly")) {            Player target;            if (!(sender instanceof Player)) {                if (args.length == 1) {                    if (!this.p.getDisplayNameOnline(args[0])) {                        sender.sendMessage(this.p.String(1, "TargetOffline", "%target% if offline.").replace("%target%", args[1]).replace("%player%", sender.getName()));                        return true;                    }                    target = this.p.getServer().getPlayer(args[0]);                    if (target.getAllowFlight()) {                        target.setAllowFlight(false);                        sender.sendMessage(this.p.String(1, "FlyFalse", "%target%'s fly is off").replace("%target%", args[0]).replace("%player%", sender.getName()));                        target.sendMessage(this.p.String(1, "FlyFalse", "%target%'s fly is off").replace("%target%", args[0]).replace("%player%", sender.getName()));                        return true;                    }                    target.setAllowFlight(true);                    sender.sendMessage(this.p.String(1, "FlyTrue", "%target%'s fly is on").replace("%target%", args[0]).replace("%player%", sender.getName()));                    target.sendMessage(this.p.String(1, "FlyTrue", "%target%'s fly is on").replace("%target%", args[0]).replace("%player%", sender.getName()));                    return true;                }                sender.sendMessage(this.p.String(1, "Usage-Fly", "Usage: /fly <player>"));                return true;            }            if (args.length == 0) {                if (!this.p.hasPermission(sender.getName(), "functions.command.fly.other")) {                    sender.sendMessage(this.p.Permission());                    return true;                }                if (((Player)sender).getPlayer().getAllowFlight()) {                    ((Player)sender).getPlayer().setAllowFlight(false);                    sender.sendMessage(this.p.String(1, "FlyFalse", "%target%'s fly is off").replace("%target%", sender.getName()).replace("%player%", sender.getName()));                    return true;                }                ((Player)sender).getPlayer().setAllowFlight(true);                sender.sendMessage(this.p.String(1, "FlyTrue", "%target%'s fly is on").replace("%target%", sender.getName()).replace("%player%", sender.getName()));                return true;            }            if (args.length == 1) {                if (!this.p.hasPermission(sender.getName(), "functions.command.fly.target")) {                    sender.sendMessage(this.p.Permission());                    return true;                }                if (!this.p.getDisplayNameOnline(args[0])) {                    sender.sendMessage(this.p.String(1, "TargetOffline", "%target% if offline.").replace("%target%", args[1]).replace("%player%", sender.getName()));                    return true;                }                target = this.p.getServer().getPlayer(args[0]);                if (target.getAllowFlight()) {                    target.setAllowFlight(false);                    sender.sendMessage(this.p.String(1, "FlyFalse", "%target%'s fly is off").replace("%target%", args[0]).replace("%player%", sender.getName()));                    target.sendMessage(this.p.String(1, "FlyFalse", "%target%'s fly is off").replace("%target%", args[0]).replace("%player%", sender.getName()));                    return true;                }                target.setAllowFlight(true);                sender.sendMessage(this.p.String(1, "FlyTrue", "%target%'s fly is on").replace("%target%", args[0]).replace("%player%", sender.getName()));                target.sendMessage(this.p.String(1, "FlyTrue", "%target%'s fly is on").replace("%target%", args[0]).replace("%player%", sender.getName()));                return true;            }        }        return true;    }}