package org.functions.Tools;import org.bukkit.GameMode;import org.bukkit.entity.Player;public class GameModeAPI {    public GameModeAPI() {    }    public static void GameModeAPI(Player Player, int GameMode) {        if (GameMode == 0) {            Player.setGameMode(org.bukkit.GameMode.SURVIVAL);        }        if (GameMode == 1) {            Player.setGameMode(org.bukkit.GameMode.CREATIVE);        }        if (GameMode == 3) {            Player.setGameMode(org.bukkit.GameMode.SPECTATOR);        }        if (GameMode == 2) {            Player.setGameMode(org.bukkit.GameMode.ADVENTURE);        }    }    public static String GameMode(int GameMode) {        String String = "";        if (GameMode == 0) {            String = "Survival";        }        if (GameMode == 1) {            String = "Creative";        }        if (GameMode == 3) {            String = "Spectator";        }        if (GameMode == 2) {            String = "Adventure";        }        return String;    }}